import React , { Component } from 'react'

class CommentInput extends Component {
		render() {
			return (
				<div>
					 <span>Title:</span>
					 <input type="text"/>
					 <span>Comment</span>
					 <textarea />
					 <button>Submit</button>
				</div>
			)
		}
}

export default CommentInput
