import React , { Component } from 'react'

class CommentInput extends Component {
	  constructor (){
			super();
			this.state = {
					username:'',
					content : ''
			}

		}
		render() {
			return (
				<div>
					 <span>Username:</span>
					 <input type="text" value="{this.state.username}"/>
					 <span>Comment</span>
					 <textarea />
					 <button>Submit</button>
				</div>
			)
		}
}

export default CommentInput
