import React , { Component } from 'react'

class Comment extends Component {
		constructor(){
				super();
				this.state = {
						comment : this.props.comment
				}
		}
		render(){
				return (
						<div>
							<span>{this.state.comment.username}</span>
							<p>{this.comment.content}</p>
						</div>
				)
		}
}

export default Comment
