import React , { Component } from 'react'
import Comment from './Comment'
class CommentList extends Component{
	render() {

			return (

					<div>
					{ this.props.comment.map((comment,i) => {
						return (
						 <div key={i}>
						 	{comment.username} :{comment.content}
						 </div>
					  )
					})
				}
				</div>
			)
	}
}

export default CommentList
